  `time` varchar(255) DEFAULT NULL COMMENT '活动时间',
  `createtime` varchar(255) DEFAULT NULL COMMENT '活动发布时间',
  `location` varchar(255) DEFAULT NULL COMMENT '活动地点',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

